
public class abecedario {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    
	for (int count=97; count<97+26;count++) {
		 System.out.print((char)count+" ");
	}
	System.out.println();
     int count=97;
		do { 
			System.out.print((char)count+" ");
			count=count+1;
		}while (count<97+26);
		
	}

}
